import React, { useState, useEffect } from 'react';
import api from './services/api';

import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';

// Componente: Bloco isolado de HTML, CSS e JS, o qual não interfere no restante da aplicação.
// Propriedades: Informaciones que un componente PAI passa para un componente FILHO.
// Estado: Infomações mantidas pelo componente (Lembrar: inmutabilidade)

function App() {  // Es un componente

  const [github_username, setGithub_username] = useState('');
  const [techs, setTechs] = useState('');
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');


  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;

        setLatitude(latitude);
        setLongitude(longitude);
      },
      (err) => {
        console.log(err);
      },
      {
        timeout: 30000,
      }
    );
  }, []);

  async function handleAddDev(e){
    // e.preventDefault();  // elimina el comportamiento default del formulario en HTML q te manda para otra tela al presionar el boton submit.
    e.preventDefault();

    const response = await api.post('/devs', {
      github_username,
      techs,
      latitude,
      longitude,
    })

    console.log(response.data);
  }

  return (
    <div id="app">
      <aside>
        <strong>Cadastrar</strong>
        <form>

          <div className="input-block">
            <label htmlFor="github_username">Usuário do GitHub</label>
            <input 
              name="github_username" 
              id="github_username" 
              required
              value={github_username}
              onChange={e => setGithub_username(e.target.value)}
              />
          </div>

          <div className="input-block">
            <label htmlFor="techs">Tecnologias</label>
            <input 
              name="techs" 
              id="techs" 
              required 
              value={techs}
              onChange={e => setTechs(e.target.value)}
              />
          </div>

          <div className="input-group">

            <div className="input-block">
              <label htmlFor="Latitude">Latitude</label>
              <input 
                type="number" 
                name="Latitude" 
                id="Latitude" 
                required 
                value={latitude}
                onChange={e => setLatitude(e.target.value)} 
              />
            </div>

            <div className="input-block">
              <label htmlFor="Longitude">Longitude</label>
              <input 
                type="number" 
                name="Longitude" 
                id="Longitude" 
                required 
                value={longitude} 
                onChange={e => setLongitude(e.target.value)}
              />
            </div>

          </div>

          <button type="submit">Salvar</button>

        </form>
      </aside>

      <main>
        <ul>
          <li className="dev-item">
            <header>
              <img src="https://avatars2.githubusercontent.com/u/35745639?s=460&v=4" alt="im"></img>
              <div className="user-info">
                <strong>Emilio Rodriguez-Hernandez</strong>
                <span>Python, Matlab, JavaScript</span>
              </div>
            </header>
            <p>I'm a telecommunication engineer that loves the computer programming. I'm pretty interested in the developing of systems and solutions.</p>
            <a href="https://github.com/emilioRguez">Accesar perfil no GitHub</a>
          </li>
          <li className="dev-item">
            <header>
              <img src="https://avatars2.githubusercontent.com/u/35745639?s=460&v=4" alt="im"></img>
              <div className="user-info">
                <strong>Emilio Rodriguez-Hernandez</strong>
                <span>Python, Matlab, JavaScript</span>
              </div>
            </header>
            <p>I'm a telecommunication engineer that loves the computer programming. I'm pretty interested in the developing of systems and solutions.</p>
            <a href="https://github.com/emilioRguez">Accesar perfil no GitHub</a>
          </li>
          <li className="dev-item">
            <header>
              <img src="https://avatars2.githubusercontent.com/u/35745639?s=460&v=4" alt="im"></img>
              <div className="user-info">
                <strong>Emilio Rodriguez-Hernandez</strong>
                <span>Python, Matlab, JavaScript</span>
              </div>
            </header>
            <p>I'm a telecommunication engineer that loves the computer programming. I'm pretty interested in the developing of systems and solutions.</p>
            <a href="https://github.com/emilioRguez">Accesar perfil no GitHub</a>
          </li>
          <li className="dev-item">
            <header>
              <img src="https://avatars2.githubusercontent.com/u/35745639?s=460&v=4" alt="im"></img>
              <div className="user-info">
                <strong>Emilio Rodriguez-Hernandez</strong>
                <span>Python, Matlab, JavaScript</span>
              </div>
            </header>
            <p>I'm a telecommunication engineer that loves the computer programming. I'm pretty interested in the developing of systems and solutions.</p>
            <a href="https://github.com/emilioRguez">Accesar perfil no GitHub</a>
          </li>
        </ul>
      </main>
    </div>
  );
}

export default App;
